import React from 'react';

import './Reviews.css';

import starIcon from 'images/star.svg';



export default function Reviews({ reviews }) {
  function rating({ star }){
    let stars = [];
    for(let i = 0; i < 5; i++){
      stars.push(<img class = "star-icon" src={starIcon} alt="star-icon" />);
    }
    return {stars}
  }

  return (
    <div class = "container-fluid">
      <div class = "row">
        <div class = "col-md-8 col-7 col-centered">
          <h2>All Reviews</h2>
          <div class = "card-group justify-content-center">

            {reviews.map(review => (

              <div class = "review-block card-header" key={review.name} data-testid="review">
                <div class = "card-header">
                  <p>{review.name}</p>
                </div>

                <div class = "card-body">
                  <p>{review.comment}</p>
                </div>


                <div class = "card-footer">

                  <p>{review.rating}</p>
                  {rating(review.rating)}
                  <img class = "star-icon" src={starIcon} alt="star-icon" />


                </div>


              </div>

            ))}

          </div>
        </div>  
      </div>
    </div>
  )
}
