import React from 'react';

import './Reviews.css';

import starIcon from 'images/star.svg';

function Repeat(props){
  let items = [];
  for (let i = 0; i < props.numTimes; i++){
    items.push(props.children(i));
  }
  return <div>{items}</div>;
}

export default function Reviews({ reviews }) {




  return (
    <div class = "container-fluid">
      <div class = "row">
        <div class = "col-md-8 col-7 col-centered">
          <h2>All Reviews</h2>
          <div class = "card-group justify-content-center">



            {reviews.map(review => (


              <div class = "review-block card-header" key={review.name} data-testid="review">
                <div class = "card-header">
                  <p>{review.name}</p>
                </div>

                <div class = "card-body">
                  <p>{review.comment}</p>
                </div>


                <div class = "card-footer">

                  <p>{review.rating}</p>
                  <Repeat numTime={5}>
                    {(index) => <div key = {index}>hhh<img class = "star-icon" src={starIcon} alt="star-icon" /></div>}
                  </Repeat>

                </div>


              </div>

            ))}

          </div>
        </div>  
      </div>
    </div>
  )
}
